mapper.select:
SELECT id,create_date createDate,modify_date modifyDate,version,class_name className,opus_num opusNum,
uuid,status,origin_value originValue,terminal_value terminalValue,description,type,goods_num goodsNum
FROM segment_category
WHERE del_flag is FALSE

    AND class_name LIKE concat('%', #{className}, '%')


    AND type = #{type}


    AND status = #{status}


    AND opus_num = #{opusNum}

ORDER BY create_date DESC

SELECT count(uuid)
FROM segment_category
WHERE del_flag is FALSE

    AND class_name LIKE concat('%', #{className}, '%')


    AND type = #{type}


    AND status = #{status}


    AND opus_num = #{opusNum}
