mapper.select:
SELECT id,create_date createDate,modify_date modifyDate,version,class_name className,orders,grade,
parent_id parentId,uuid,status,description,type,goods_num goodsNum
FROM skirt_category
WHERE del_flag is FALSE

    AND type = #{type}


    AND parent_id = #{parentId}


    AND grade = #{grade}

ORDER BY create_date DESC

SELECT count(uuid)
FROM skirt_category
WHERE del_flag is FALSE

    AND type = #{type}


    AND parent_id = #{parentId}


    AND grade = #{grade}
